function mapValues(value, minInput, maxInput, minOutput, maxOutput) {
    // Asegurarse de que el valor esté dentro del rango de entrada
    value = Math.max(minInput, Math.min(maxInput, value));

    // Calcular la proporción del valor en el rango de entrada
    const ratio = (value - minInput) / (maxInput - minInput);

    // Escalar la proporción al rango de salida y redondear al entero más cercano
    return Math.round(minOutput + ratio * (maxOutput - minOutput));
}

